      do member=0,(hidnodes-1)
         unit=member+inpnodes
         sum=0.0
         do source=0,(inpnodes-1)
            sum=sum+(act(source)*weights((member*inpnodes)+source))
         enddo
         if((sum+biases(unit)).LT.10000.0) THEN
            act(unit)=1/(1+exp(-sum-biases(unit)))
         else
            act(unit)=0.0
         endif
