      do member=0,(outnodes-1)
         unit=member+inpnodes+hidnodes
         sum=0.0
         do source=0,(hidnodes-1)
            sum=sum+(act(source+inpnodes)*
     &           weights((inpnodes*hidnodes)+source))
         enddo
         if((sum+biases(unit)).LT.10000.0) THEN
            act(unit)=1/(1+exp(-sum-biases(unit)))
         else
            act(unit)=0.0
         endif
      enddo

C the result       
      do member=0,(outnodes-1)
         unit=member+inpnodes+hidnodes
         out(member)=act(unit)
      enddo

      RETURN
      end
